class Day9_2 {

    class Node(val name: String) {
        var children = mutableListOf<Node>()
        var visited = false
    }


    fun ladderLength(beginWord: String, endWord: String, wordList: List<String>): Int {
        val origin = Node(beginWord)
        val nodes = mutableListOf(origin)
        for (i in 0 until wordList.size) {
            if (wordList[i] != beginWord) {
                nodes.add(Node(wordList[i]))
            }
        }
        // complex optimization available. This is brute force approach first.
        for (i in 0 until nodes.size) {
            for (j in 0 until nodes.size) {
                if (diff(nodes[i].name, nodes[j].name) == 1) {
                    nodes[i].children.add(nodes[j])
                }
            }
        }

        val nodeQueue: Queue<Node> = LinkedList<Node>()
        nodeQueue.add(origin)

        // for every node, we enqueue the children and increment the level
        var level = 1
        while (!nodeQueue.isEmpty()) {

            for (i in 0 until nodeQueue.size) {
                val node = nodeQueue.remove()
                // visit the node
                if (node.name == endWord) {
                    return level
                }
                node.visited = true
                // children added to end of queue, we will process the parents first
                for (n in node.children) {
                    if (!n.visited) {
                        nodeQueue.add(n)
                    }
                }
            }
            level++
        }
        return 0
    }

    private fun diff(word: String, target: String): Int {
        var count = 0
        for (i in 0 until word.length) {
            if (word[i] != target[i]) {
                count++
            }
        }
        return count
    }
}